import React, { Component } from 'react';
import './App.css';
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';
import { useCallback } from "react";
import Particles from "react-particles";
import 'tachyons';
import Clarifai from 'clarifai';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';


const PAT = '1f99a3573f0143219eee1f52aed0a5dd';
const USER_ID = 'erinbejta';       
const APP_ID = 'my-first-application-t9kdyj';
const MODEL_ID = 'face-detection';
const MODEL_VERSION_ID = '6dc7e46bc9124c5c8824be4822abe105';    


class App extends Component{
  constructor(){
    super();
    this.state = {
      input: '',
      imageUrl: '',
      box: {},
    }
  }

  

  calculateFaceLocation = (data) => {
  const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box;
  const image = document.getElementById('inputimage');
  const width = Number(image.width);
  const height = Number(image.height);
  return{
   leftCol: clarifaiFace.left_col * width,
   topRow: clarifaiFace.top_row * height,
   rightCol: width - (clarifaiFace.right_col * width),
   bottomRow: height - (clarifaiFace.bottom_row * height),
  }
  console.log(width,height);
    
  }

  onInputChange = (event) => {
    this.setState({input: event.target.value});

  }


  onButtonSubmit = () => {
    this.setState({imageUrl: this.state.input})
     const raw = JSON.stringify({
        "user_app_id": {
            "user_id": USER_ID,
            "app_id": APP_ID
        },
        "inputs": [
            {
                "data": {
                    "image": {
                        "url": this.state.input
                    }
                }
            }
        ]
    });


    const requestOptions = {
        method: 'POST',
        headers: {
            'Accept': 'application/json',
            'Authorization': 'Key ' + PAT
        },
        body: raw
    };

    // NOTE: MODEL_VERSION_ID is optional, you can also call prediction with the MODEL_ID only
    // https://api.clarifai.com/v2/models/{YOUR_MODEL_ID}/outputs
    // this will default to the latest version_id

    fetchClarifaiData = () => {
  fetch("https://api.clarifai.com/v2/models/" + MODEL_ID + "/versions/" + MODEL_VERSION_ID + "/outputs", requestOptions)
    .then(response => response.json())
    .then(result => {
      const boundingBoxData = result.outputs[0].data.regions[0].region_info.bounding_box;

      // Now that you have the bounding box data, you can call your function
      this.calculateFaceLocation(boundingBoxData);

      // You can also set the data in the component's state if needed
      
    })
    .catch(error => console.log('error', error));
}

  }

  render(){

 return (
    <div className="App">
     <Navigation />
     <Logo />
     <Rank />
     <ImageLinkForm 
     onInputChange={this.onInputChange}
     onButtonSubmit={this.onButtonSubmit}
      />
    <FaceRecognition imageUrl={this.state.input}/> 
    </div>

  );

}
}

 
export default App;
